function getData() {
    function getPhotoCount() {
        return +document.querySelector('#photoCount').value;
    }
    if (getPhotoCount() <= 0) {
        return;
    }
    async function getPhotos() {
        try {
            let response = await fetch('http://jsonplaceholder.typicode.com/photos');
            let images = await response.json();
            images = images.slice(0, getPhotoCount());
            images.forEach((element, i) => {
                $('#image-container')
                    .append(`
                    <div class="test">${element.title}</div>
                    <img src='${element.url}'>
                `);
            });
            $('#image-container').slick({
                slidesToShow: 1,
                slidesToScroll: 1,
                autoplay: true,
                autoplaySpeed: 3000,
            });
        } catch (error) {}
    }
    let divT = document.createElement('div');
    divT.innerText = `Выведено ${getPhotoCount()} картинок!`;
    document.getElementById("image-container").append(divT);
};

$(document).ready(function() {
    document.getElementById("getPhoto").addEventListener("click", (e) => {
        getData();
    });
});